mapper.select:
select * from user


        id = #{selectMessage};


        username like concat('%',#{selectMessage},'%');


        sex = 1;


        sex = 0;


        email like concat('%',#{selectMessage},'%');

select count(*) from user


        id = #{selectMessage};


        username like concat('%',#{selectMessage},'%')


        sex = 1;


        sex = 0;


        email like concat('%',#{selectMessage},'%');

mapper.update:
update user set

    username = #{value}


    password = #{value}


    email = #{value}


    avatar = #{value}


    introduction = #{value}


    topic_count = #{value}


    comment_count = #{value}


    rank_id = #{value}


    support_count = #{value}


    rank_level = #{value}


    sex = #{value}


    browse_count = #{value}


    secret = #{value}

where id = #{id};
